import cv2import numpy as npdigits = cv2.imread("/Users/mehdimirawa/Desktop/video IA/digit/digits.png",cv2.IMREAD_GRAYSCALE)test_digits = cv2.imread("/Users/mehdimirawa/Desktop/video IA/digit/test_digits.png",cv2.IMREAD_GRAYSCALE)#############my_digit = cv2.imread()rows = np.vsplit(digits,50)###############cv2.imshow("row 0",rows[0])#cv2.waitKey(0)#cv2.destroyAllWindows()##############cells = []cells2 = []for row in rows:    row_cells = np.hsplit(row,100)    for cell in row_cells:        cells.append(cell)                cells2.append(cell.flatten())        cells2 = np.array(cells2,dtype=np.float32)test_cells = np.vsplit(test_digits,50)test_cells_flat = []for digit in test_cells:    test_cells_flat.append(digit.flatten())    test_cells_flat=np.array(test_cells_flat,dtype=np.float32)################n = np.arange(10)targets=np.repeat(n,500)################knn = cv2.ml.KNearest_create()knn.train(cells2,cv2.ml.ROW_SAMPLE,targets)################ret,result,neighbours,dist=knn.findNearest(test_cells_flat,k=3)