import cv2import numpy as npdigits = cv2.imread("/Users/mehdimirawa/Desktop/video IA/digit/digits.png",cv2.IMREAD_GRAYSCALE)test_digit = cv2.imread("/Users/mehdimirawa/Desktop/video IA/digit/test_digits.png",cv2.IMREAD_GRAYSCALE)#############my_digit = cv2.imread()rows = np.vsplit(digits,50)###############cv2.imshow("row 0",rows[0])#cv2.waitKey(0)#cv2.destroyAllWindows()##############cells = []cells2 = []for row in rows:    row_cells = np.hsplit(row,100)    for cell in row_cells:        cells.append(cell)                        cells2.append(cell.flatten())        